namespace Cubiche\Core\EventDispatcher\Tests\Units;

use Cubiche\Core\EventDispatcher\Event;

/**
 * Event class.
 *
 * Generated by TestGenerator on 2016-04-11 at 15:18:25.
 */
class EventTests extends TestCase
{
    /**
     * Test Name method.
     */
    public function testName()
    {
        $this
            ->given($event = new Event())
            ->when($name = $event->eventName())
            ->then()
                ->string($name)
                    ->isEqualTo(Event::class)
        ;

        $this
            ->given($event = new Event('foo.event'))
            ->when($name = $event->eventName())
            ->then()
                ->string($name)
                    ->isEqualTo('foo.event')
        ;
    }

    /**
     * Test Named method.
     */
    public function testNamed()
    {
        $this
            ->given($event = Event::named('foo.event'))
            ->when($name = $event->eventName())
            ->then()
                ->string($name)
                    ->isEqualTo('foo.event')
        ;
    }

    /**
     * Test StopPropagation method.
     */
    public function testStopPropagation()
    {
        $this
            ->given($event = new Event())
            ->then()
                ->boolean($event->isPropagationStopped())
                    ->isFalse()
            ->and()
            ->when($event->stopPropagation())
            ->then()
                ->boolean($event->isPropagationStopped())
                    ->isTrue()
        ;
    }
}
